import React, { Component, useState, useRef, useEffect } from 'react';
import styles from  './App.css';

import Navbar from 'react-bootstrap/Navbar'
import Nav from 'react-bootstrap/Nav';
import Main from './pages/main';
import FooterPanel from './components/footer/footer.component';
import {Link,NavLink} from 'react-router-dom';


function TestNav() {
  const [change, setChange] = useState(false);
  const changePosition = 300;

  let position = useWindowScrollPosition();
  // position == { x: 0, y: 0 }

  if (position.y > changePosition && !change) {
    setChange(true);
  }

  if (position.y <= changePosition && change) {
    setChange(false);
  }

  let style = {
    backgroundColor: change ? "blue" : "transparent",
    transition: "400ms ease",
    height: "100px",
    position: "fixed",
    right: 0,
    left: 0,
    top: 0
  };

  return (
    <div style={style}>
      <h1>Logo</h1>
    </div>
  );
}


class App extends Component {
render() {
  return(
    <div>
      <Navbar bg="light" expand="md" sticky="top" className="animate-navbar nav-theme justify-content-between" style={style}>
      <Navbar.Brand href="/home">Portfolio</Navbar.Brand>
      <Navbar.Toggle aria-controls="basic-navbar-nav" />
      <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="ml-auto">
          <Nav.Link as={NavLink} to ="/home" href="/home">HOME</Nav.Link>
            <Nav.Link as={NavLink} to="/experience" href="/experience">EXPERIENCE</Nav.Link>
            <Nav.Link as={NavLink} to="/projects" href="/projects">PROJECTS</Nav.Link>
            <Nav.Link as={NavLink} to="/education" href="/education">EDUCATION</Nav.Link>
            <Nav.Link as={NavLink} to="/skills" href="/skills">SKILLS</Nav.Link>
            <Nav.Link as={NavLink} to="/Resume" href="/Resume">RESUME</Nav.Link>
        </Nav>
      </Navbar.Collapse>
    </Navbar>
      <Main />
    <FooterPanel />
    </div>
  );
}
}

export default App;
